INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (153, 'Vichy Nutrilogie 2', 124.00, 'src/img/product156.webp', 0, 0, 6, 4, 2, 'AQUA, GLYCERIN, PRUNUS ARMENIACA KERNEL OIL, ISOCETYL STEARATE, BUTYROSPERMUM PARKII BUTTER, SUCROSE TRISTEARATE, HYDROGENATED POLYISOBUTENE, DIMETHICONE, PETROLATUM, PENTYLENE GLYCOL, POLYSORBATE 61, CERA ALBA, STEARYL ALCOHOL, BHT, STEARIC ACID, MACADAMIA TERNIFOLIA SEED OIL, CARBOMER, GLYCINE SOJA OIL, TRIETHANOLAMINE, CORIANDRUM SATIVUM FRUIT OIL, ARGININE PCA, MYRISTIC ACID, CHOLESTEROL, 2-OLEAMIDO-1,3-OCTADECANEDIOL, PALMITIC ACID, SIMMONDSIA CHINENSIS SEED OIL, PHENOXYETHANOL, TOCOPHEROL, DISODIUM EDTA, CAPRYLYL GLYCOL, HYDROXYPALMITOYL SPHINGANINE, CITRIC ACID, PASSIFLORA EDULIS SEED OIL, PARFUM (F.I.L. C221066/1).', 'Suferiti din cauza tenului inrosit, intins si pruriginos? Solutia este crema pentru ten foarte uscat Vichy Nutrilogie 2. Datorita continutului de sfingolipide, hraneste intens tenul care nu poate produce singur o cantitate suficienta de grasime. Dupa utilizare, tenul devin imediat moale, hidratat si multumit. Confera tenului uscat recompensa nutritiva pe care o merita cu adevarat.', 'Se aplica dimineata, pe pielea bine curatata. Pentru cele mai bune rezultate, combinati cu o crema de noapte din linia Vichy Nutrilogie.', 'https://www.notino.ro/vichy/nutrilogie-2-crema-pentru-ten-uscata-si-foarte-uscata/', 12);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (154, 'Vichy Slow Age', 150.00, 'src/img/product157.webp', 0, 35, 6, 5, 7, 'AQUA / WATER, OCTOCRYLENE, GLYCERIN, HOMOSALATE, ETHYLHEXYL SALICYLATE, BIFIDA FERMENT LYSATE, ALCOHOL DENAT., BUTYL METHOXYDIBENZOYLMETHANE, DIMETHICONE, SUCROSE TRISTEARATE, PENTAERYTHRITYL TETRAETHYLHEXANOATE, SILICA, TOCOPHEROL, ISOPROPYL LAUROYL SARCOSINATE, SYNTHETIC WAX, POLYSORBATE 61, CETYL ALCOHOL, HDI/TRIMETHYLOL HEXYLLACTONE CROSSPOLYMER, CI 17200 / RED 33, CI 77891 / TITANIUM DIOXIDE, CI 77491, CI 77492, CI 77499 / IRON OXIDES, STEARIC ACID, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, DIMETHICONOL, TRIETHANOLAMINE, PARFUM ', 'Ingrijirea Vichy Slow Age ofera tenului hidratare instantanee, il regenereaza si il netezeste. Lasa pielea hranita si tonifiata, stralucind de sanatate si de tinerete. Indispensabila protectie solara SPF 30 protejeaza pielea fata de radiatiile UV.', 'Aplicati in fiecare dimineata, pe tenul curat si uscat. Evitati zona de contur a ochilor.', 'https://www.notino.ro/vichy/slow-age-tratament-zilnic-anti-imbatranire-spf-30/', 0);
