INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (161, 'MAC Cosmetics Retro Matte Lipstick', 99.00, 'src/img/product164.webp', 1, 0, 16, 5, 22, null, 'V-ati plictisit de rujuri stralucitoare si de gloss-uri de buze? Emblematicul ruj cu efect matifiant, MAC Retro Matte Lipstick, va oferi buzelor dvs. cu totul altceva. Acesta le va conferi o culoare intensa si un finisaj sofisticat si deosebit de mat. In plus, va rezista pana la 8 ore pe buze, fara a se sterge sau a se estompa.', 'Aplicati rujul pe buze, printr-o miscare delicata, incepand din mijloc si continuand pana in colturi. Pentru o aplicare mai precisa, folositi o pensula.', 'https://www.notino.ro/mac/retro-matte-ruj-cu-efect-matifiant/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (162, 'MAC Cosmetics Powder Kiss Liquid Lipcolour', 129.00, 'src/img/product165.webp', 1, 0, 16, 5, 25, 'DIMETHICONE, DIMETHICONE CROSSPOLYMER, ISONONYL ISONONANOATE, POLYGLYCERYL-2 TRIISOSTEARATE, DIISOSTEARYL MALATE, TRIBEHENIN, TOCOPHERYL ACETATE, LECITHIN, ETHYLHEXYL PALMITATE, ISOPROPYL MYRISTATE, POLYGLYCERYL-3 POLYRICINOLEATE, POLYGLYCERYL-2 DIISOSTEARATE, SORBITAN ISOSTEARATE, DEHYDROACETIC ACID, ISOSTEARIC ACID, POLYHYDROXYSTEARIC ACID, VANILLIN', 'Cu rujul lichid mat MAC Cosmetics Powder Kiss Liquid Lipcolour, va veti putea bucura de buze nemaipomenit de delicate si fara cusur. Textura sa usoara, ca de spuma, se aplica deosebit de usor pe buze, hidratandu-le instantaneu si conferindu-le o culoare intensa. In plus, rezultatul perfect si senzatia placuta vor reziste ore in sir.', 'Aplicati rujul cu ajutorul aplicatorului in forma de clepsidra.', 'https://www.notino.ro/mac-cosmetics/powder-kiss-liquid-lipcolour-ruj-lichid-mat/p-16055125/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (163, 'MAC Cosmetics Pro Longwear Concealer', 124.00, 'src/img/product166.webp', 1, 0, 16, 5, 14, null, 'Corectorul lichid usor MAC Pro Longwear va ajuta sa camuflati toate imperfectiunile tenului. Cu toate ca ofera o acoperire medie pana la opaca, arata foarte natural si matifiaza placut tenul. Puteti acoperi cu usurinta cosuri, cearcane si coloratii neuniforme. Bucurati-va de aspectul perfect al tenului dvs., timp de pana la 15 ore!
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (164, 'Estee Lauder Bronze Goddess', 191.00, 'src/img/product167.webp', 1, 0, 17, 5, 15, null, 'Simtiti-va ca o zeita a frumusetii atunci cand va machiati. Iluminatorul Estee Lauder Bronze Goddess confera chipului o stralucire si luminozitate asemanatoare perlelor. Accentuand delicat zonele fetei, iluminatorul scoate in evidenta cele mai frumoase trasaturi, pentru a va putea bucura de un aspect sanatos si plin de vitalitate intreaga zi.', 'Cu o pensula pentru iluminator se aplica pe zonele cele mai inalte ale fetei, respectiv pe pometi, varful nasului, sub sprancene si in coltul intern al ochilor.', 'https://www.notino.ro/estee-lauder/bronze-goddess-iluminator/', 0);
