INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (186, 'Vichy Liftactiv B3 Anti - Dark Spots', 248.00, 'src/img/product189.webp', 0, 35, 6, 5, 7, null, 'Imbatranirea, poluarea si razele UV duc adesea la aparitia petelor intunecate si a ridurilor, iar tenul poate suferi, in plus, de nuanta neuniforma. Crema intensiva antirid Vichy Liftactiv B3 Anti-Dark Spots, imbogatita cu niacinamida si substanta activa LHA, corecteaza petele intunecate zi de zi si previne aparitia lor ulterioara. Hidrateaza intensiv, confera tenului fermitate si, datorita SPF 50, il protejeaza de razele UV. 
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (187, 'Estee Lauder Revitalizing Supreme+ Global Anti-Aging Cell Power Creme', 487.00, 'src/img/product190.webp', 0, 35, 17, 5, 7, null, 'Cheia unui ten mai tanar, mai ferm si mai luminos. Aceasta este crema multifunctionala antirid, cu extract de moringa, SPF 15 Estee Lauder Revitalizing Supreme +, care atenueaza atat ridurile fine, cat si pe cele profunde si favorizeaza regenerarea si reinnoirea celulelor epiteliale, pentru un ten neted si luminos.', 'Crema Estee Lauder Revitalizing Supreme Plus se aplica pe fata si pe decolteu. Se foloseste dimineata si seara, de preferinta peste un ser regenerator.', 'https://www.notino.ro/estee-lauder/advanced-night-repair-crema-anti-rid-cu-extract-de-moringa-spf-15/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (188, 'DIOR Dior Forever', 245.00, 'src/img/product191.webp', 1, 0, 12, 5, 12, '#15866 AQUA (WATER), METHYL TRIMETHICONE, ISODODECANE, ALCOHOL, BUTYLENE GLYCOL, TITANIUM DIOXIDE, PEG-9 POLYDIMETHYLSILOXYETHYL DIMETHICONE, ETHYLHEXYL SALICYLATE, SYNTHETIC FLUORPHLOGOPITE, ACRYLATES/POLYTRIMETHYLSILOXYMETHACRYLATE COPOLYMER, SILICA, DIMETHICONE, ACRYLATES/DIMETHICONE COPOLYMER, IRIS FLORENTINA ROOT EXTRACT, DISTEARDIMONIUM HECTORITE, POLYGLYCERYL-10 DECAISOSTEARATE, CETYL DIMETHICONE, SODIUM MYRISTOYL GLUTAMATE, PARFUM (FRAGRANCE), PROPYLENE CARBONATE, CHLORPHENESIN, ALUMINA, STEARIC ACID', 'Tenul este uniformizat, porii sunt vizibil estompati, iar pielea pare netezita. Datorita formulei curate*, cu o concentratie ridicata de ingrediente florale cu rol de ingrijire, cu grad ridicat de confort, fondul de ten ii permite pielii sa respire: calitatea pielii este imbunatatita zi de zi.', '1. Incepeti prin a aplica fondul de ten tamponand zonele care necesita corectare, apoi estompati prin netezire.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (189, 'Clinique Anti-Blemish Solutions Liquid Makeup', 129.00, 'src/img/product192.webp', 1, 0, 19, 5, 12, null, 'Acum, si femeile cu ten problematic pot savura ritualul zilnic de machiaj. Fondul de ten pentru ten problematic, cu tendinta spre acnee, Clinique Anti-Blemish Solutions acopera imperfectiunile, reduce roseata si uniformizeaza tonul pielii. Toate, fara a incarca excesiv tenul si fara a astupa porii, pentru un ten uniform si fresh.', 'Agitati inainte de utilizare. Aplicati uniform pe fata si estompati folosind o pensula, un aplicator de burete sau varfurile degetelor. Daca este necesar, stratificati, pentru o acoperire mai mare.', 'https://www.notino.ro/clinique/anti-blemish-solutions-make-up-lichid-ten-cu-probleme-acnee/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (190, 'Maybelline SuperStay Active Wear', 56.00, 'src/img/product193.webp', 1, 0, 14, 5, 12, null, 'Bucurati-va de un machiaj rezistent chiar si in cele mai active zile. Cu fondul de ten Maybelline SuperStay Active Wear nu este nevoie sa va faceti griji in ceea ce priveste refacerea machiajului pe parcursul zilei. Cu o rezistenta de 30 de ore la apa si transpiratie, formula lejera acopera eficient zonele cu imperfectiuni, oferind tenului un aspect mat si natural.
