INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (192, 'Maybelline Fit Me! Matte+Poreless', 32.00, 'src/img/product195.webp', 1, 0, 14, 5, 13, null, 'Un aspect impecabil al machiajului cu ajutorul unei tuse finale potrivite oricarui tip de ten. Pudra Maybelline Fit Me! Matte + Poreless absoarbe excesul de sebum si ofera tenului un aspect mat, perfect. Datorita tehnologiei Perlite Mineral, pudra lasa pe piele un finisaj natural si nu se strange in liniile fine sau cute.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (193, 'Clinique Stay-Matte Sheer Pressed Powder', 155.00, 'src/img/product196.webp', 1, 0, 19, 1, 13, null, 'Daca aveti tenul lucios, pudra matifianta pentru ten gras Clinique Stay Matte nu trebuie sa va lipseasca din arsenalul cosmetic. Previne luciul, matifiaza perfect tenul si il mentine proaspat, chiar si dupa cateva ore de la aplicare. In plus, fixeaza perfect machiajul si uniformizeaza nuanta tenului. Ambalajul practic si compact va sta intotdeauna la indemana. Astfel, luciul nedorit nu are nicio sansa.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (194, 'DIOR Forever Natural Velvet', 266.00, 'src/img/product197.webp', 1, 0, 12, 5, 13, '#17007 SYNTHETIC FLUORPHLOGOPITE, MICA, CALCIUM SODIUM BOROSILICATE, SILICA, BUTYLENE GLYCOL DICAPRYLATE/DICAPRATE, BORON NITRIDE, ZINC STEARATE, CAPRYLIC/CAPRIC/SUCCINIC TRIGLYCERIDE, POLYGLYCERYL-2 TRIISOSTEARATE, PENTYLENE GLYCOL, AQUA (WATER), CAPRYLYL GLYCOL, IRIS FLORENTINA ROOT EXTRACT, ETHYLHEXYLGLYCERIN, BUTYLENE GLYCOL, SODIUM DEHYDROACETATE, PARFUM', 'Dior Forever Natural Velvet este fondul de ten compact antitransfer Dior cu o formula bazata in proportie de 90%* pe ingrediente de origine naturala, pentru un ten mat impecabil cu o rezistenta de 24 de ore**. Textura sa inovatoare*** este catifelata, lejera si confortabila, pentru a lasa pielea sa respire fara a o usca, reducand in acelasi timp luciul. Acoperirea modulabila, de la aspect natural la acoperire totala, uniformizeaza tenul pentru un efect neted si imbunatateste textura pielii.', '1. Aplicati fondul de ten cu tuse mici pe fata cu ajutorul partii catifelate a aplicatorului.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (195, 'DIOR Forever Skin Correct', 165.00, 'src/img/product198.webp', 1, 0, 12, 5, 14, '#17440 AQUA (WATER), C9-12 ALKANE, CAPRYLIC/CAPRIC TRIGLYCERIDE, PROPANEDIOL, GLYCERIN, POLYGLYCERYL-6 POLYRICINOLEATE, DICAPRYLYL ETHER, PENTYLENE GLYCOL, SILICA, POLYGLYCERYL-10 DECAISOSTEARATE, SYNTHETIC FLUORPHLOGOPITE, TRIMETHYLSILOXYSILICATE, IRIS FLORENTINA ROOT EXTRACT, POLYGLYCERYL-2 ISOSTEARATE, DISTEARDIMONIUM HECTORITE, SODIUM MYRISTOYL GLUTAMATE, STEARALKONIUM HECTORITE, SODIUM CHLORIDE, BUTYLENE GLYCOL, LECITHIN', 'Descoperiti Dior Forever Skin Correct, un concealer Dior antitransfer, cu acoperire ridicata si formula curata*, alcatuita conform unei liste stricte de ingrediente, din care 96%** sunt de origine naturala, pentru 2 de ore*** de hidratare si rezistenta.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (196, 'Estee Lauder Double Wear Radiant Concealer', 145.00, 'src/img/product199.webp', 1, 0, 17, 5, 14, null, 'Ascundeti imperfectiunile pielii si oferiti fetei un aspect fara cusur cu anticearcanul Estee Lauder Double Wear Radiant. Formula cremoasa si confortabila, uniformizeaza tonul pielii si reduce imperfectiunile, conferind un aspect neted si un finisaj radiant. Extractele botanice si uleiurile esentiale de chia din compozitie, hraneste intens pielea si ilumineaza zona ochilor, in timp ce ascunde cearcanele si reduce roseata.
