INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (203, 'MUA Makeup Academy Brow Define', 17.00, 'src/img/product206.webp', 1, 0, 11, 5, 20, 'OZOKERITE, POLYETHYLENE, MICA, C10-18 TRIGLYCERIDE, CAPRYLIC/CAPRIC TRIGLYCERIDE, BIS-DIGLYCERYL POLYACYLADIPATE-2, COPERNICIA CERIFERA CERA, PHENOXYETHANOL, CAPRYLYL GLYCOL, TOCOPHERYL ACETATE, CI 77891, CI 7499, CI 77492, CI 77491.', 'Sprancenele sunt foarte importante pentru intregul aspect. Totusi, daca natura nu a fost foarte generoasa cu dvs. in aceasta privinta, nu va fie teama sa le oferiti o mana de ajutor. Creionul persistent cu periuta MUA Makeup Academy Brow Define va va evidentia sprancenele. Este suficient sa ii ajustati forma si sa umpleti spatiile dintre firele de par.', null, 'https://www.notino.ro/mua-makeup-academy/brow-define-creion-de-sprancene-de-lunga-durata-cu-pensula/', 0);
