INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (209, 'MAC Cosmetics Lip Pencil', 101.00, 'src/img/product212.webp', 1, 0, 16, 5, 23, null, 'Pentru a va putea bucura de buze superbe, nu uitati sa va definiti contururile cu un creion de buze, inainte de a aplica rujul. Acesta va contura perfect buzele, le va accentua forma si va impiedica rujul sa estompeze. Creionul de buze MAC Cosmetics Lip Pencil are o compozitie cremoasa si delicata, se aplica foarte usor si nu pateaza. Un produs ideal.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (210, 'Maybelline SuperStay Matte Ink', 41.00, 'src/img/product213.webp', 1, 0, 14, 5, 25, null, 'Rasfatati-va buzele cu o culoare super intensa, care rezista chiar si 16 ore! Rujul mat lichid Maybelline SuperStay Matte Ink coloreaza buzele in nuante saturate, cu un finisaj de catifea mata.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (211, 'LOreal Paris Infallible Les Macarons', 59.00, 'src/img/product214.webp', 1, 0, 5, 5, 25, null, 'Oferiti buzelor dvs. o recompensa dulce. Aroma de migdale, de vanilie si de frisca a rujului lichid, persistent si mat LOreal Infallible Les Macarons va transporta intr-o clipa intr-o cofetarie frantuzeasca. Aplicatorul in forma de inimioara intinde usor textura cremoasa si intens pigmentata pe buze, fara sa lase o senzatie lipicioasa.', 'Folosind aplicatorul, intindeti rujul din centrul buzelor spre margini. Repetati aplicarea pe parcursul zilei, daca este nevoie.', 'https://www.notino.ro/loreal-paris/loreal-paris-infaillible-les-macarons-ruj-de-buze-lichid-mat-si-de-lunga-durata/p-15961383/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (212, 'Maybelline SuperStay Vinyl Ink', 53.00, 'src/img/product215.webp', 1, 0, 14, 5, 25, 'SODODECANE, DIMETHICONE, TRIMETHYLSILOXYSILICATE, POLYMETHYLSILSESQUIOXANE/TRIMETHYLSILOXYSILICATE, POLYPROPYLSILSESQUIOXANE, C30-45 ALKYLDIMETHYLSILYL POLYPROPYLSILSESQUIOXANE, TRIMETHYLSILOXYPHENYL DIMETHICONE, TRIMETHYL PENTAPHENYL TRISILOXANE, BIS-STEARYL DIMETHICONE, DIMETHICONOL, TRIHYDROXYSTEARIN', 'Cautati un ruj persistent, cu o culoare saturata si un luciu intens? Rujul lichid, Maybelline SuperStay Vinyl Ink, bifeaza toate cerintele si demonstreaza ca nu doar finisajul mat, ci si cel lucios poate rezista pe buze intreaga zi, fara estompare. Culoarea intens pigmentata si lucioasa nu se transfera, ci ramane pe buze pana la 16 ore.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (213, 'DIOR Rouge Dior Forever Liquid', 186.00, 'src/img/product216.webp', 1, 0, 12, 5, 25, null, 'Rouge Dior Forever Liquid este primul ruj Dior lichid ultrapigmentat si rezistent la transfer, in culori vibrante si ultramate. Nuantele uimitoare ale rujului lichid nu se aduna in liniile buzelor, nu se estompeaza, nu migreaza si nu se striaza, chiar si pe tesaturi sau masti.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (214, 'MAC Cosmetics Lipglass', 111.00, 'src/img/product217.webp', 1, 0, 16, 5, 24, null, 'Suculente, stralucitoare, ispititoare. Exact asa vor arata buzele dvs. dupa aplicarea luciului de buze MAC Cosmetics Lipglass. In doar cateva secunde, buzele vor straluci incredibil si persistent. Aplicati-l doar in centrul buzelor, pentru un efect plin seducator, sau peste rujul preferat, pentru o nota sic. Rezultatul nu va va dezamagi niciodata.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (215, 'Clinique Pop Plush Creamy Lip Gloss', 99.00, 'src/img/product218.webp', 1, 0, 19, 5, 24, null, 'Buzele dvs. merita sa arate frumoase si irezistibile. Luciul de buze Clinique Pop Splash Lip Gloss + Hydration ajuta la netezirea si ingrijirea buzelor, asigurandu-le o hidratare imediata si de lunga durata pe tot parcursul zilei. Dupa aplicare, buzele devin mai moi si mai hidratate, cu un aspect sanatos si o stralucire senzationala.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (216, 'MUA Makeup Academy Lip Gloss Nourishing', 18.00, 'src/img/product219.webp', 1, 0, 11, 5, 24, 'ETHYLHEXYL PALMITATE, HYDROGENATED POLYDECENE, POLYISOBUTENE, CAPRYLIC/CAPRIC TRIGLYCERIDE, TRIDECYL TRIMELLITATE, HYDROGENATED STYRENE/ISOPRENE COPOLYMER, SILICA DIMETHYL SILYLATE, PHENOXYETHANOL, SIMMONDSIA CHINENSIS SEED OIL, ROSA CANINA FRUIT OIL, ARGANIA SPINOSA KERNEL OIL, PARFUM, TOCOPHERYL ACETATE, ALUMINUM HYDROXIDE, ETHYLHEXYLGLYCERIN, BENZYL ALCOHOL, BENZYL SALICYLATE, HEXYL CINNAMAL, HYDROXYCITRONELLAL, CI 77891, CI 77491, CI 77492, CI 77499, CI 15850, CI 19140.', 'Luciul de buze MUA Makeup Academy Lip Gloss Nourishing da buzelor un aspect minunat.
