INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (218, 'Clinique Pop Lip Colour + Primer', 103.00, 'src/img/product221.webp', 1, 0, 19, 5, 22, null, 'Cand se combina un ruj de o culoare intensa si bogata cu o baza hidratanta si nutritiva, apare o combinatie geniala pentru buzele dvs. Rujul + baza 2 in 1 Clinique Pop este usor de aplicat si creeaza un finisaj moale si catifelat. In plus, hidrateaza si revitalizeaza buzele.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (219, 'DIOR Rouge Dior', 176.00, 'src/img/product222.webp', 1, 0, 12, 5, 22, '#15057/A HYDROGENATED POLYDECENE, DIMETHICONE, POLYETHYLENE, DIMETHICONE CROSSPOLYMER, SILICA, VINYL DIMETHICONE/METHICONE SILSESQUIOXANE CROSSPOLYMER, EUPHORBIA CERIFERA (CANDELILLA) WAX, ETHYLHEXYL PALMITATE, BUTYROSPERMUM PARKII (SHEA) BUTTER, PARFUM (FRAGRANCE), PRUNUS AMYGDALUS DULCIS (SWEET ALMOND) OIL, BUTYROSPERMUM PARKII (SHEA) BUTTER UNSAPONIFIABLES, PAEONIA OFFICINALIS FLOWER EXTRACT, SIMMONDSIA CHINENSIS (JOJOBA) SEED OIL', 'Rouge Dior este rujul Dior in culori couture. Oferindu-le buzelor o ingrijire florala autentica, este imbogatit cu extracte de origine naturala de floare de bujor rosu si de rodiu.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (220, 'DIOR Addict', 188.00, 'src/img/product223.webp', 1, 0, 12, 5, 22, '#16229 METHYL HYDROGENATED ROSINATE, SQUALANE, POLYGLYCERYL-2 TRIISOSTEARATE, HELIANTHUS ANNUUS (SUNFLOWER) SEED WAX, DIMER DILINOLEYL DIMER DILINOLEATE, HYDROGENATED STYRENE/METHYLSTYRENE/INDENE COPOLYMER, SORBITAN OLIVATE, STEARYL HEPTANOATE, TRIMETHYLOLPROPANE TRIISOSTEARATE, CERA ALBA (BEESWAX), STEARYL CAPRYLATE, SUCROSE TETRASTEARATE TRIACETATE, ORYZA SATIVA (RICE) BRAN WAX, CALCIUM ALUMINUM BOROSILICATE', 'Rujul Dior Addict se reinnoieste intr-o versiune reincarcabila. Decorat in varf cu o perla argintie si ornamentat cu literele Dior Oblique, tubul de ruj Dior Addict devine un accesoriu fashion in sine.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (221, 'Estee Lauder Pure Color Matte Lipstick', 177.00, 'src/img/product224.webp', 1, 0, 17, 5, 22, null, 'Rujul Estee Lauder Pure Color Matte Lipstick accentueaza frumos buzele si subliniaza perfect fondul de ten de zi si de seara.', 'Rujul se aplica pe buze cu tuse delicate, dinspre centru spre colturi.', 'https://www.notino.ro/estee-lauder/pure-color-matte-lipstick-ruj-cu-persistenta-indelungata-cu-efect-mat/p-16185454/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (222, 'MAC Cosmetics Liquidlast 24 Hour Waterproof Liner', 117.00, 'src/img/product225.webp', 1, 0, 16, 5, 21, 'ISODODECANE, TRIMETHYLSILOXYSILICATE, DIMETHICONE, CALCIUM ALUMINUM BOROSILICATE, DISTEARDIMONIUM HECTORITE, SORBITAN TRISTEARATE, SILICA, PROPYLENE CARBONATE, ASCORBYL PALMITATE, TIN OXIDE, CAPRYLYL GLYCOL, HEXYLENE GLYCOL, PHYTOSTERYL ISOSTEARATE, HYDROGENATED PALM KERNEL GLYCERIDES, HYDROGENATED PALM GLYCERIDES, PENTAERYTHRITYL TETRA-DI-T-BUTYL', 'Privire intensa, perfect conturata. Tusul MAC Cosmetics Liquidlast 24 Hour Waterproof Liner are o rezistenta la apa si transfer, ce impiedica difuzia acestuia pe pleoapa. Datorita aplicatorului fin puteti crea linii de grosimi diferite, pornind de la cele mai discrete si subtiri pana la umplere groasa. Culoarea sa opaca este intensa si nu necesita reaplicarea.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (223, 'MUA Makeup Academy Eye Define', 21.00, 'src/img/product226.webp', 1, 0, 11, 5, 21, 'AQUA, ACRYLATES COPOLYMER, PROPYLENE GLYCOL, BUTYLENE GLYCOL, AMMONIUM ACRYLATES COPOLYMER, CAPRYLYL GLYCOL, PHENOXYETHANOL, METHYLPROPANEDIOL, HYDROXYETHYLCELLULOSE, ETHYLHEXYLGLYCERIN, SIMETHICONE, PHENYLPROPANOL, CI 77266 (NANO).', 'Nu conteaza daca urmariti doar un look natural de fiecare zi sau un machiaj dramatic de femme fatale. Conturul de ochi tip carioca MUA Makeup Academy Eye Define nu va dezamageste niciodata, iar rezultatul va fi intotdeauna conform asteptarilor dvs.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (224, 'MAC Cosmetics Brushstroke 24 Hour Liner', 102.00, 'src/img/product227.webp', 1, 0, 16, 5, 21, null, 'Privire conturata intr-o nuanta vibranta. Tusul pentru ochi MAC Cosmetics Brushstroke 24 Hour Liner creeaza un look dramatic si atractiv, conferind seductie privirii. Asigura o aplicare usoara si precisa datorita varfului special creat care poate trasa tuse delicate sau groase, in functie de machiajul pe care doriti sa il realizati. Rezista pana la 24 de ore, nu se intinde si se demachiaza simplu, fara frecare.', 'Aplicati cu tuse delicate, direct pe linia genelor, printr-o miscare ferma si sigura. In functie de grosimea dorita, puteti insista.', 'https://www.notino.ro/mac-cosmetics/brushstroke-24-hour-liner-creion-pentru-conturul-ochilor/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (225, 'Bioderma Sebium Pore Refiner', 68.00, 'src/img/product228.webp', 0, 0, 8, 1, 2, 'AQUA/WATER/EAU, CETEARYL ALCOHOL, OCTYLDODECANOL, PANTHENOL, BETAINE, PROPYLENE GLYCOL DIBENZOATE, BEHENTRIMONIUM CHLORIDE, STEARETH-2, MANNITOL, XYLITOL, RHAMNOSE, FRUCTOOLIGOSACCHARIDES, LAMINARIA OCHROLEUCA EXTRACT, TRIDECETH-9 PGAMODIMETHICONE, HYDROXYETHYLCELLULOSE, TRIDECETH-12, CAPRYLIC/ CAPRIC TRIGLYCERIDE, PENTYLENE GLYCOL, 1,2-HEXANEDIOL, CAPRYLYL GLYCOL, DISODIUM EDTA, FRAGRANCE (PARFUM), BENZYL SALICYLATE, LINALOOL. [BI 630]', 'Un singur produs - o mare diferenta! Daca aveti tenul gras, acest produs va va veni in ajutor, aducand un efect matifiant tenului dvs.', 'Aplicati zilnic, pe tenul curatat in prealabil.', 'https://www.notino.ro/bioderma/sebium-pore-refiner-ser-pentru-ten-pentru-ten-mixt-si-gras/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (226, 'La Roche-Posay Effaclar H', 93.00, 'src/img/product229.webp', 0, 0, 9, 1, 2, 'AQUA / WATER / EAU, GLYCERIN, SQUALANE, DIMETHICONE, PANTHENOL, ZEA MAYS STARCH / CORN STARCH, NIACINAMIDE, AMMONIUM POLYACRYLOYLDIMETHYL TAURATE, MYRISTYL MYRISTATE, BIXA ORELLANA SEED EXTRACT, STEARIC ACID, POTASSIUM CETYL PHOSPHATE, GLYCERYL STEARATE SE, SODIUM HYDROXIDE, MYRISTIC ACID, 2-OLEAMIDO-1,3-OCTADECANEDIOL, PALMITIC ACID, HYDROXYACETOPHENONE, CAPRYLOYL GLYCINE, CAPRYLYL GLYCOL, VITREOSCILLA FERMENT, CITRIC ACID, MALTODEXTRIN, XANTHAN GUM, PARFUM / FRAGRANCE ', 'O ingrijire corecta pentru un ten cu aspect sanatos. Crema hidratanta La Roche-Posay Effaclar H asigura tenului gras cu tendinta acneica o nutritie avansata, prevenind cu succes principalele probleme cauzate de tratamentele agresive.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (227, 'La Roche-Posay Effaclar Mat', 105.00, 'src/img/product230.webp', 0, 0, 9, 1, 2, 'AQUA / WATER, GLYCERIN, DIMETHICONE, ISOCETYL STEARATE, ALCOHOL DENAT., SILICA [NANO] / SILICA, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, ACRYLAMIDE/SODIUM ACRYLOYLDIMETHYLTAURATE COPOLYMER, METHYL METHACRYLATE CROSSPOLYMER, BUTYLENE GLYCOL, PEG-100 STEARATE, COCAMIDE MEA, SARCOSINE, GLYCERYL STEARATE, TRIETHANOLAMINE, ISOHEXADECANE, PERLITE, CAPRYLOYL SALICYLIC ACID, TETRASODIUM EDTA, PENTYLENE GLYCOL, POLYSORBATE 80, ACRYLATES/C10-30 ALKYL ACRYLATE CROSSPOLYMER, SALICYLIC ACID, PARFUM / FRAGRANCE ', 'Efect dublu pentru tenul gras si problematic. Ingrijirea matifianta La Roche-Posay Effaclar Mat contracta porii si combate eficient productia excesiva de sebum la adolescenti si la adulti. Imediat dupa aplicare, pielea este hidratata, luciul este suprimat, iar matifierea tenului este durabila.', 'Se aplica dimineata sau seara, pe tenul curat, si se lasa sa se absoarba. Evitati zona ochilor. Se poate folosi si ca baza de machiaj, sub fondul de ten.', 'https://www.notino.ro/la-roche-posay/effaclar-crema-hidratanta-pentru-ten-gras/', 0);
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (228, 'Vichy Normaderm Phytosolution', 101.00, 'src/img/product231.webp', 0, 0, 6, 1, 2, 'AQUA / WATER / EAU, GLYCERIN, BUTYLENE GLYCOL, SALICYLIC ACID, ISONONYL ISONONANOATE, KAOLIN, HYDROLYZED ALGIN, ZINC SULFATE, BIFIDA FERMENT LYSATE, SARCOSINE, SODIUM HYALURONATE, SODIUM HYDROXIDE, TRISODIUM ETHYLENEDIAMINE DISUCCINATE, ASCORBYL GLUCOSIDE, ACETIC ACID, ACRYLATES/C10-30 ALKYL ACRYLATE CROSSPOLYMER, BIOSACCHARIDE GUM-1, CAPRYLYL GLYCOL, LACTIC ACID, MALTODEXTRIN, PENTYLENE GLYCOL, SODIUM POLYACRYLATE, XANTHAN GUM, SODIUM BENZOATE, PARFUM / FRAGRANCE ', 'Incercati efectul dublu al tratamentului local cu actiune rapida Vichy Normaderm Phytosolution. Contine substante active de origine naturala, care combat eficient imperfectiunile tenului cauzate de acnee. In plus, ajuta la refacerea pielii si o fortifica impotriva influentelor ambientale. Textura de tip gel, ultra-usoara si fresh, cu aroma fina de menta, este foarte placuta pe piele si o lasa sa respire liber.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (229, 'Estee Lauder DayWear Multi-Protection Anti-Oxidant 24H-Moisture Creme', 136.00, 'src/img/product232.webp', 0, 0, 17, 3, 2, 'AVOBENZONE 3.00%, OCTISALATE 5.00% WATER/AQUA/EAU, DIMETHICONE, BUTYLOCTYL SALICYLATE, BUTYLENE GLYCOL, POLYESTER-8, CETYL RICINOLEATE, STEARETH-21, STEARETH-2, DI-C12-15 ALKYL FUMARATE, CAPRYLIC/CAPRIC/MYRISTIC/STEARIC TRIGLYCERIDE, POLYSILICONE-11, PSIDIUM GUAJAVA (GUAVA) FRUIT EXTRACT, GENTIANA LUTEA (GENTIAN) ROOT EXTRACT, POLYGONUM CUSPIDATUM ROOT EXTRACT, STEARYL ALCOHOL, HORDEUM VULGARE (BARLEY) EXTRACTEXTRAIT D\'ORGE', 'Luati cea mai buna decizie la alegerea ingrijirii adecvate tenului tanar. Toate cele necesare pentru combaterea primelor semne de imbatranire sunt continute de crema de zi protectoare, pentru ten mixt, Estee Lauder DayWear.
INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (230, 'Vichy Liftactiv Supreme', 169.00, 'src/img/product233.webp', 0, 0, 6, 3, 2, 'AQUA / WATER, GLYCERIN, DIMETHICONE, RHAMNOSE, ISOHEXADECANE, ALCOHOL DENAT., PROPANEDIOL, ISOPROPYL ISOSTEARATE, VINYL DIMETHICONE/METHICONE SILSESQUIOXANE CROSSPOLYMER, CETYL ALCOHOL, DIMETHICONE/VINYL DIMETHICONE CROSSPOLYMER, BEHENYL ALCOHOL, NYLON-12, PEG-100 STEARATE, CI 77891 / TITANIUM DIOXIDE, STEARIC ACID, STEARYL ALCOHOL, SORBITAN OLEATE, ARACHIDYL ALCOHOL, CETEARYL ALCOHOL, CETEARYL GLUCOSIDE, CAFFEINE, NEOHESPERIDIN DIHYDROCHALCONE, MYRISTYL ALCOHOL, MYRISTIC ACID, PALMITIC ACID, PHENOXYETHANOL, ADENOSINE', 'Va prezentam prima crema cu efect de lifting pentru intreaga zi, de la Vichy, pentru compensarea constanta a ridurilor si fermitate. Crema de zi cu efect de lifting pentru ten normal si mixt Vichy Liftactiv Supreme va ofera un aspect mai tanar, regenereaza tenul din interior si reduce vizual imperfectiunile si ridurile, imediat dupa aplicare. Rezultatul este un ten fin si perfect neted.
