INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (253, 'Clinique Smart Night Custom-Repair Moisturizer', 276.00, 'src/img/product256.webp', 0, 35, 19, 4, 7, '', 'Crema hidratanta Clinique Clinique Smart Night lucreaza toata noaptea, netezeste vizibil ridurile, si face pielea mai ferma.', 'Aplicati seara pe fata bine curatata (pe gat si decolteu, dupa nevoie), masati usor. Nu clatiti.', 'https://www.notino.ro/clinique/clinique-smart-crema-de-noapte-hidratanta-anti-rid-ten-uscat-si-mixt/', 1);
