INSERT INTO database_web.products (product_id, name, price, image_path, is_makeup, age, brand_id, skintype_id, type_id, ingredients, description, how_to_use, link, times_rec) VALUES (271, 'MAC Cosmetics Eye Brows Big Boost Fiber Gel', 123.00, 'src/img/product274.webp', 1, 0, 16, 5, 19, null, 'Volumul si culoarea sprancenelor vor fi blocate in loc toata ziua si toata noaptea! Gelul colorat pentru sprancene MAC Cosmetics Eye Brows Big Boost Fiber Gel contine microfibre sintetice mici care ofera un impuls de volum instantaneu, in timp ce definesc si modeleaza sprancenele pentru un machiaj reusit dintr-o singura lovitura. Cu o formula impermeabila, rezistenta la pete, transpiratie si decolorare, gelul creeaza sprancene cu aspect mai plin, cu o rezistenta de pana la 24 de ore fara descuamare. 
